(441, 0, NULL, 1, 18, 7, 'Detail produktu', 1, 1, 5, 5, 'detail-produktu', 0, 1, '<div class=\"productDetail\">\n  <div class=\"row productName\">\n    <div class=\"col-md-12\">\n      <h1>{Name} <span class=\"<%HideIfIsEmpty(\'{Color}\')%>\">- {Color}</span></h1>\n      </div>\n  </div>\n  <div class=\"row\">\n    <div class=\"col-md-6 productImage\">\n      <a href=\"/<%GetBigFile({MainImage})%>\" title=\"\" data-gallery=\"\" data-role=\"none\" data-ajax=\"false\">\n<img src=\"/<%GetMediumFile({MainImage})%>\" alt=\"{Name} {Color}\" title=\"{Name} {Color}\">\n</a>\n\n      \n		\n    </div>\n	<div class=\"col-md-6 productInfo\">\n      <div class=\"form-horizontal\">\n      \n      <div class=\"form-group <%HideIfIsEmpty(\'{Vat}\')%> \">\n       	\n          <label class=\"control-label col-md-3 \">\">\n          Cena s {Vat}% DPH:\n            </label>\n          \n        \n        <div class=\"col-md-9 \">\">\n          <%PriceVat(\'{Price}\',\'cs_CZ\',\'%.2n\',\'{Vat}\')%>\n        </div>\n      </div>\n      \n      <div class=\"form-group <%HideIfIsEmpty(\'{Price}\')%>\">\n       	\n          <label class=\"control-label col-md-3\">\n          Cena:\n            </label>\n        \n        <div class=\"col-md-9 \">\n          <%FormatPrice(\'{Price}\',\'cs_CZ\',\'%.2n\')%>\n        </div>\n      </div>\n            <div class=\"form-group <%HideIfIsEmpty(\'{Color}\')%>\">\n       	\n          <label class=\"control-label col-md-3\">\n          Barva:\n            </label>\n        \n        <div class=\"col-md-9\">\n          {Color}\n        </div>\n      </div>\n                    \n        \n                            <div class=\"form-group <%HideIfIsEmpty(\'{ProductStock}\')%>\">\n       	\n          <label class=\"control-label col-md-3\">\n          Dostupnost produktu:\n            </label>\n        \n        <div class=\"col-md-9\">\n          <%ProductStock(\'{ProductStock}\')%>\n        </div>\n      </div>\n                                  <div class=\"form-group <%HideIfIsEmpty(\'{SlozeniProduct}\')%>\">\n       	\n          <label class=\"control-label col-md-3\">\n          Složení:\n            </label>\n        \n        <div class=\"col-md-9\">\n          {SlozeniProduct}\n                                    </div>\n      </div>\n		   <div class=\"form-group <%HideIfIsEmpty(\'{ProductDescription}\')%>\">\n       	\n          <label class=\"control-label col-md-3\">\n          \n            </label>\n        \n        <div class=\"col-md-9\">\n          {ProductDescription}\n                                    </div>\n      </div>\n                              \n        \n        \n  		\n  		</div>\n      	\n      <div class=\"form-horizontal {SelectSize-dn}\">\n      	<div class=\"form-group\">\n          <label for=\"SelectSize\" class=\"control-label col-md-3\">Velikost:</label>\n          <div class=\"col-md-9\">\n            <xWeb:Component Type=\"DomainItemSelectBox\" SelectId=\"SelectSize\" ValueIdetificator=\"Size\" CssClass=\"form-control\" Id=\"SelectSize\"/>\n          </div>\n        </div>\n      </div>\n      \n         \n      <xWeb:Component Type=\"AddToCart\" SelectItem=\"SelectSize\" ButtonName=\"word785\" ErrorMessage=\"tatiana7\" CartUrl=\"/kosik1008/\" />\n  		\n    </div>\n  </div>\n</div>\n<div class=\"tabs\">\n  \n<ul class=\"nav nav-tabs\">\n  <li class=\"active <%HideIfIsEmpty(\'{Description}\')%> description\"><a data-toggle=\"tab\" href=\"#description\"  >Informace o produktu</a></li>\n  <li><a data-toggle=\"tab\" class=\"{VariantList-dn} variants\" href=\"#variants\">Varianty</a></li>\n  <li><a data-toggle=\"tab\" class=\"{FormQuestion-dn} productQuestion\" href=\"#productQuestion\">Dotaz k produktu</a></li>\n  <li><a data-toggle=\"tab\" class=\"{Gallery-dn} productGallery\" href=\"#productGallery\">Gallerie</a></li>\n  <li><a data-toggle=\"tab\" class=\"{RelatedProducts-dn} relatedProducts \" href=\"#relatedProducts\">Souvisejicí produkty</a></li>\n</ul>\n\n<div class=\"tab-content\">\n  <div id=\"description\" class=\"tab-pane fade in active <%HideIfIsEmpty(\'{Description}\')%>\">\n    <div class=\"description producttabcontent\">{Description}</div>\n  </div>\n    <div id=\"variants\" class=\"tab-pane fade {VariantList-dn}\">\n      <div class=\"producttabcontent\">\n    	<xWeb:Component Type=\"VariantList\" Id=\"VariantList\" AcceptUserTeplates=\"ProductDetail\"/>\n      </div>\n  </div>\n  <div id=\"productQuestion\" class=\"tab-pane fade {FormQuestion-dn}\">\n      <div class=\"producttabcontent\">\n    	<xWeb:Component Type=\"Form\" Id=\"FormQuestion\"/>\n      </div>\n  </div>\n  <div id=\"productGallery\" class=\"tab-pane fade {Gallery-dn}\">\n      <div class=\"producttabcontent\">\n        <xWeb:Component  Type=\"FileList\" FileType=\"gallery\" VariantFileName=\"ImageGallery\" Id=\"Gallery\"/>\n      </div>\n  </div>\n  <div id=\"relatedProducts\" class=\"tab-pane fade {RelatedProducts-dn}\">\n      <div class=\"producttabcontent\">\n        <xWeb:Component  Type=\"RelatedArticles\" VariantFileName=\"Product\" Id=\"RelatedProducts\"/>\n      </div>\n  </div>\n  \n</div>\n</div>\n      <div id=\"blueimp-gallery\" class=\"blueimp-gallery\">\n    <div class=\"slides\"></div>\n    <h3 class=\"title\"></h3>\n    <a class=\"prev\">‹</a>\n    <a class=\"next\">›</a>\n    <a class=\"close\">×</a>\n    <a class=\"play-pause\"></a>\n    <ol class=\"indicator\"></ol>\n</div>\n      <script type=\"text/javascript\">\n        $(document).ready(function(){\n          	if (!$(\".tab-content #description\").hasClass(\"dn\"))\n            {\n               $(\".tab-content #description\").addClass(\"active\");\n               $(\".tab-content #description\").addClass(\"in\");\n               $(\".nav .nav-tabs .description\").addClass(\"active\");\n            }\n          \n          	else if (!$(\".tab-content #variants\").hasClass(\"dn\"))\n            {\n               $(\".tab-content #variants\").addClass(\"active\");\n               $(\".tab-content #variants\").addClass(\"in\");\n              $(\".nav .nav-tabs .variants\").addClass(\"active\");\n            }\n          else if (!$(\".tab-content #productQuestion\").hasClass(\"dn\"))\n            {\n               $(\".tab-content #productQuestion\").addClass(\"active\");\n               $(\".tab-content #productQuestion\").addClass(\"in\");\n              $(\".nav .nav-tabs .productQuestion\").addClass(\"active\");\n            }\n          else if (!$(\".tab-content #productGallery\").hasClass(\"dn\"))\n            {\n               $(\".tab-content #productGallery\").addClass(\"active\");\n               $(\".nav .nav-tabs .productGallery\").addClass(\"active\");\n               $(\".tab-content #productGallery\").addClass(\"in\");\n            }\n          else if (!$(\".tab-content #relatedProducts\").hasClass(\"dn\"))\n            {\n               $(\".tab-content #relatedProducts\").addClass(\"active\");\n               $(\".tab-content #relatedProducts\").addClass(\"in\");\n              $(\".nav .nav-tabs .relatedProducts\").addClass(\"active\");\n            }\n        });\n      </script>\n      \n', '                                \n            \n            ', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '2017-07-14 10:19:22', '<items><hideAlernativeObjects><![CDATA[1]]></hideAlernativeObjects></items>'),
